CREATE DATABASE PROYECTOV3;
USE PROYECTOV3;

CREATE TABLE documental
(
  Titulo VARCHAR(255) NOT NULL,
  Año INT NOT NULL,
  Paises VARCHAR(100),
  Duracion_en_minutos INT NOT NULL,
  Director VARCHAR(50),
  Sinopsis TEXT,
  Semblanza TEXT,
  Iniciativas VARCHAR(50),
  Tematica VARCHAR(255),
  Festivales_y_premios TEXT,
  PRIMARY KEY (Titulo)
);
alter table documental modify column TEMATICA VARCHAR(255);

SELECT * FROM documental;

CREATE TABLE creditos
(
  Titulo VARCHAR(255) PRIMARY KEY,
  Fotografias VARCHAR(255),
  Produccion VARCHAR(255),
  Guion VARCHAR(50),
  Produccion_Ejecutiva VARCHAR(255),
  Edicion VARCHAR(255),
  Sonido VARCHAR(255),
  Compania_productora VARCHAR(255),
  FOREIGN KEY (Titulo) REFERENCES Documental(Titulo)
);

alter table creditos modify column TITULO VARCHAR(255);

CREATE TABLE Autor
(
  AutorID INT AUTO_INCREMENT,
  Nombre VARCHAR(255) NOT NULL,
  Ap_Paterno VARCHAR(255) NOT NULL,
  PRIMARY KEY (AutorID)
);


CREATE TABLE Dirigir
(
  AutorID INT NOT NULL,
  Titulo VARCHAR(255) NOT NULL,
  PRIMARY KEY (AutorID, Titulo),
  FOREIGN KEY (AutorID) REFERENCES Autor(AutorID),
  FOREIGN KEY (Titulo) REFERENCES Documental(Titulo)
);


CREATE TABLE Festival
(
  Nombre VARCHAR(255),
  Lugar VARCHAR(255),
  Fecha DATE,
  Descripcion TEXT,
  Costo INT,
  PRIMARY KEY (Nombre)
);


CREATE TABLE Secciones
(
  Nombre_de_la_seccion VARCHAR(255),
  Calendario DATE NOT NULL,
  Descripcion TEXT,
  Documental VARCHAR(255),
  Festival VARCHAR(255),
  PRIMARY KEY (Nombre_de_la_seccion, Documental),
  FOREIGN KEY (Documental) REFERENCES Documental(Titulo),
  FOREIGN KEY (Festival) REFERENCES Festival(Nombre)
);

